@mixin global__Flex($flex-direction, $justify-content) {
	display: flex;
	flex-direction: $flex-direction;
	justify-content: $justify-content;
}

@mixin allBtn {
	.btn__Circle {
		border-radius: 50%;
		width: 130px;
		height: 130px;

		box-shadow: inset 0 -5px rgba(0, 0, 0, 0.3);
		border: none;

		margin: 0 auto;
		cursor: pointer;

		transition: 1s;

		@include media('>=tablet') {
			width: 170px;
			height: 170px;
		}
	}
	.wrapper {
		background-color: white;
		border-radius: 50%;

		box-shadow: inset 0 5px rgba(0, 0, 0, 0.2);

		display: inline-flex;
		align-items: center;
		justify-content: center;

		width: 100px;
		height: 100px;

		@include media('>=tablet') {
			width: 130px;
			height: 130px;
		}
	}

	.paper {
		background: $paper-Gradient;
	}
	.scissors {
		background: $scissors-Gradient;
	}
	.rock {
		background: $rockGradient;
	}
}

